export class Validation{constructor(t,e,i){this.form=document.getElementById(t),this.inputs=e.map(t=>document.getElementById(t)),this.errors=i.map(t=>document.getElementById(t)),this.form.addEventListener("submit",t=>{t.preventDefault(),this.validateAll()}),this.inputs.forEach((t,e)=>{t.addEventListener("input",()=>{this.validateField(e)})})}validateAll(){this.inputs.forEach((t,e)=>this.validateField(e))}validateField(t){let e=[this.validateName,this.validateEmail,this.validateSubject,this.validateMessage];e[t](this.inputs[t].value)?this.errors[t].setAttribute("hidden","true"):this.errors[t].removeAttribute("hidden")}validateName(t){return/^[a-zA-Z ]+$/.test(t)}validateEmail(t){return/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(t)}validateSubject(t){return/^[\w ]{6,}$/.test(t)}validateMessage(t){return/^[\w ]{10,}$/.test(t)}}